import React from 'react';
import styled from 'styled-components';
import { MyAnimation } from "./animation/Animations.tsx";

type StyleBtnPropsType = {
	color
}

function App() {
	return (
		<div className="App">
			<Menu>
				<ul>
					<li><a href="#">menu item 1</a></li>
					<li><a href="#">menu item 2</a></li>
					<li><a href="#">menu item 3</a></li>
				</ul>
			</Menu>
			<StyledDiv>
				<StyledBtn as="a" href="#">Link</StyledBtn>
				<StyledBtn as={Link} href="#">Link-Link</StyledBtn>

				<StyledBtn color="green">Hello</StyledBtn>
				<StyledBtn>Hello</StyledBtn>
				<NewBtn>Yappp!</NewBtn>
			</StyledDiv>
		</div>
	);
}

export default App;

const StyledBtn = styled.button`
border:none;
background-color:${props => props.color || "blue"}; //динамически менять стили через пропсы. Если не заданы пропсы то выводится по умолчанию свойство
padding: 10px 20px;
border-radius: 5px;
color: aliceblue;
margin-right: 70px;

&:hover {
	background-color:red;
}
`

const NewBtn = styled(StyledBtn)`
	background-color: bisque;
	color: blueviolet;

	&:hover {
	animation: ${MyAnimation} 2s ease-in-out;// добавление анимации кнопке
	}
`

const Link = styled(StyledBtn)`
	background-color: blue;
	color: burlywood;
`

const StyledDiv = styled.div`
	display: flex;
	align-items: center;
	justify-content: space-around;
	height: auto;

	button { //обращение к дочернему элементу
		cursor: pointer;
	}

	${Link} { //обращение к элементу внутри другого элемента
		cursor: zoom-in;
	}	

	@media (max-width: 880px) {// медиа-запросы адаптивность - пишутся прямо в компоненте
		flex-direction: column;
		gap: 30px;
	}
`

const Menu = styled.nav`
	border: 1px solid blue;

	ul {
		list-style: none;
		padding: 0;
		margin: 0;
		display: flex;

		li > a {
			color: green;
		}

		li + li {
			margin-left: 30px;
		}
	}
`
